import React, { useState, useEffect, useCallback } from "react";
import publicationService from "../api/publicationService";
import LoadingSpinner from "../components/LoadingSpinner";

const HomePage = () => {
  const [publications, setPublications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filters, setFilters] = useState({
    title: "",
    authorName: "",
    category: "",
    year: "",
    sortBy: "recent",
    limit: 10,
  });

  const fetchPublications = useCallback(async () => {
    setLoading(true);
    setError("");
    try {
      const params = { ...filters, page: currentPage };
      // Remove empty filter values
      Object.keys(params).forEach((key) => {
        if (params[key] === "" || params[key] == null) {
          delete params[key];
        }
      });
      const data = await publicationService.getPublishedPublications(params);
      setPublications(data.data || []);
      setTotalPages(data.pagination?.totalPages || 1);
    } catch (err) {
      setError(
        err.response?.data?.error ||
          err.message ||
          "Failed to fetch publications."
      );
      setPublications([]);
    } finally {
      setLoading(false);
    }
  }, [currentPage, filters]);

  useEffect(() => {
    fetchPublications();
  }, [fetchPublications]);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
    setCurrentPage(1);
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchPublications();
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Hero Section */}
      <div className="relative bg-gradient-to-r from-blue-600 to-purple-700 h-96">
        <div className="absolute inset-0 bg-black/40 flex items-center justify-center">
          <div className="text-center text-white px-4 max-w-4xl">
            <h1 className="text-4xl md:text-6xl font-bold mb-6">
              Explore Academic Excellence
            </h1>
            <p className="text-lg md:text-xl text-gray-200">
              Discover groundbreaking research, insightful publications, and
              scholarly works from leading experts worldwide.
            </p>
          </div>
        </div>
      </div>

      <main className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-12">
        {/* Filters Section */}
        <section className="mb-12">
          <h2 className="sr-only">Publication Filters</h2>
          <form
            onSubmit={handleFilterSubmit}
            className="bg-white rounded-xl shadow-lg p-6 border border-gray-100"
          >
            <div className="grid grid-cols-1 md:grid-cols-3 lg:grid-cols-6 gap-4">
              <input
                type="text"
                name="title"
                placeholder="Title"
                value={filters.title}
                onChange={handleFilterChange}
                className="input-field"
              />
              <input
                type="text"
                name="authorName"
                placeholder="Author"
                value={filters.authorName}
                onChange={handleFilterChange}
                className="input-field"
              />
              <select
                name="category"
                value={filters.category}
                onChange={handleFilterChange}
                className="input-field"
              >
                <option value="">All Categories</option>
                <option value="magazine">Magazine</option>
                <option value="book">Book</option>
                <option value="research_paper">Research Paper</option>
                <option value="journal">Journal</option>
              </select>
              <input
                type="number"
                name="year"
                placeholder="Year"
                value={filters.year}
                onChange={handleFilterChange}
                className="input-field"
                min="1900"
                max={new Date().getFullYear()}
              />
              <select
                name="sortBy"
                value={filters.sortBy}
                onChange={handleFilterChange}
                className="input-field"
              >
                <option value="recent">Most Recent</option>
                <option value="dateOfPublication_desc">Newest Published</option>
                <option value="dateOfPublication_asc">Oldest Published</option>
                <option value="title_asc">Title A-Z</option>
                <option value="title_desc">Title Z-A</option>
              </select>
              <button type="submit" className="btn-primary">
                Apply Filters
              </button>
            </div>
          </form>
        </section>

        {/* Content Section */}
        <section>
          {error && (
            <div className="mb-8 p-4 bg-red-50 text-red-700 rounded-lg">
              {error}
            </div>
          )}

          {loading ? (
            <div className="flex justify-center items-center h-64">
              <LoadingSpinner />
            </div>
          ) : publications.length > 0 ? (
            <>
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6 mb-8">
                {publications.map((pub) => (
                  <PublicationCard key={pub._id} publication={pub} />
                ))}
              </div>
              <Pagination
                currentPage={currentPage}
                totalPages={totalPages}
                onPageChange={handlePageChange}
              />
            </>
          ) : (
            <div className="text-center py-12 bg-white rounded-lg shadow-sm">
              <p className="text-gray-500 text-lg">
                No publications found matching your criteria.
              </p>
            </div>
          )}
        </section>
      </main>
    </div>
  );
};

// Publication Card Component
const PublicationCard = ({ publication }) => (
  <div className="group bg-white rounded-xl shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
    <div className="p-6">
      <div className="flex items-center justify-between mb-4">
        <span className="inline-block px-3 py-1 text-sm font-semibold text-blue-800 bg-blue-100 rounded-full">
          {publication.category}
        </span>
        <span className="text-sm text-gray-500">{publication.year}</span>
      </div>
      <h3 className="text-xl font-bold text-gray-900 mb-2 group-hover:text-blue-600 transition-colors duration-200">
        {publication.title}
      </h3>
      <p className="text-gray-600 mb-4">{publication.authorName}</p>
      <div className="flex items-center justify-between">
        <button className="text-blue-600 hover:text-blue-800 font-medium transition-colors duration-200">
          View Details â†’
        </button>
      </div>
    </div>
  </div>
);

// Pagination Component
const Pagination = ({ currentPage, totalPages, onPageChange }) => {
  const pages = Array.from({ length: totalPages }, (_, i) => i + 1);

  return (
    <div className="flex justify-center items-center gap-2 mt-8">
      {pages.map((page) => (
        <button
          key={page}
          onClick={() => onPageChange(page)}
          className={`px-4 py-2 rounded-md ${
            currentPage === page
              ? "bg-blue-600 text-white"
              : "bg-white text-gray-700 hover:bg-gray-100"
          } transition-colors duration-200`}
        >
          {page}
        </button>
      ))}
    </div>
  );
};

// Tailwind CSS Classes (add to your global CSS file)
/*
@layer components {
  .input-field {
    @apply w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-blue-500 outline-none transition-all;
  }
  
  .btn-primary {
    @apply w-full bg-blue-600 text-white px-6 py-2 rounded-lg hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:ring-offset-2 transition-colors duration-200;
  }
}
*/

export default HomePage;
