import React, { useState, useEffect, useCallback } from "react";
import publicationService from "../api/publicationService";
import PublicationCard from "../components/PublicationCard";
import LoadingSpinner from "../components/LoadingSpinner";
import Pagination from "../components/Pagination";

const HomePage = () => {
  const [publications, setPublications] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState("");
  const [currentPage, setCurrentPage] = useState(1);
  const [totalPages, setTotalPages] = useState(1);
  const [filters, setFilters] = useState({
    title: "",
    authorName: "",
    category: "",
    year: "",
    sortBy: "recent",
    limit: 10,
  });

  const fetchPublications = useCallback(async () => {
    setLoading(true);
    setError("");
    try {
      const params = { ...filters, page: currentPage };
      Object.keys(params).forEach((key) => {
        if (
          params[key] === "" ||
          params[key] === null ||
          params[key] === undefined
        ) {
          delete params[key];
        }
      });
      const data = await publicationService.getPublishedPublications(params);
      setPublications(data.data || []);
      setTotalPages(data.pagination?.totalPages || 1);
    } catch (err) {
      setError(
        err.response?.data?.error ||
          err.message ||
          "Failed to fetch publications."
      );
      setPublications([]);
    } finally {
      setLoading(false);
    }
  }, [currentPage, filters]);

  useEffect(() => {
    fetchPublications();
  }, [fetchPublications]);

  const handleFilterChange = (e) => {
    setFilters({ ...filters, [e.target.name]: e.target.value });
    setCurrentPage(1);
  };

  const handleFilterSubmit = (e) => {
    e.preventDefault();
    fetchPublications();
  };

  const handlePageChange = (page) => {
    setCurrentPage(page);
  };

  return (
    <div className="max-w-7xl mx-auto px-4 py-8">
      <h1 className="text-3xl font-bold mb-6 text-center">Published Works</h1>

      <form
        onSubmit={handleFilterSubmit}
        className="mb-6 p-6 border border-gray-300 rounded-lg bg-white shadow-sm"
      >
        <div className="flex flex-wrap gap-4">
          <input
            type="text"
            name="title"
            placeholder="Filter by Title"
            value={filters.title}
            onChange={handleFilterChange}
            className="border border-gray-300 rounded-md px-3 py-2 w-full sm:w-48 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <input
            type="text"
            name="authorName"
            placeholder="Filter by Author"
            value={filters.authorName}
            onChange={handleFilterChange}
            className="border border-gray-300 rounded-md px-3 py-2 w-full sm:w-48 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <select
            name="category"
            value={filters.category}
            onChange={handleFilterChange}
            className="border border-gray-300 rounded-md px-3 py-2 w-full sm:w-48 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="">All Categories</option>
            <option value="magazine">Magazine</option>
            <option value="book">Book</option>
            <option value="research_paper">Research Paper</option>
            <option value="journal">Journal</option>
          </select>
          <input
            type="number"
            name="year"
            placeholder="Year (YYYY)"
            value={filters.year}
            onChange={handleFilterChange}
            className="border border-gray-300 rounded-md px-3 py-2 w-full sm:w-36 focus:outline-none focus:ring-2 focus:ring-blue-500"
          />
          <select
            name="sortBy"
            value={filters.sortBy}
            onChange={handleFilterChange}
            className="border border-gray-300 rounded-md px-3 py-2 w-full sm:w-56 focus:outline-none focus:ring-2 focus:ring-blue-500"
          >
            <option value="recent">Most Recent (Upload)</option>
            <option value="dateOfPublication_desc">
              Newest (Publication Date)
            </option>
            <option value="dateOfPublication_asc">
              Oldest (Publication Date)
            </option>
            <option value="title_asc">Title (A-Z)</option>
            <option value="title_desc">Title (Z-A)</option>
          </select>
          <button
            type="submit"
            className="bg-blue-600 text-white px-4 py-2 rounded-md hover:bg-blue-700 transition"
          >
            Apply Filters
          </button>
        </div>
      </form>

      {error && <p className="text-red-500 text-center mb-4">{error}</p>}
      {loading ? (
        <div className="flex justify-center items-center h-40">
          <LoadingSpinner />
        </div>
      ) : publications.length > 0 ? (
        <>
          <div className="grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-6">
            {publications.map((pub) => (
              <div
                key={pub._id}
                className="transition-transform transform hover:-translate-y-1 hover:shadow-lg"
              >
                <PublicationCard publication={pub} />
              </div>
            ))}
          </div>
          <div className="mt-6">
            <Pagination
              currentPage={currentPage}
              totalPages={totalPages}
              onPageChange={handlePageChange}
            />
          </div>
        </>
      ) : (
        <p className="text-gray-600 text-center">No publications found.</p>
      )}
    </div>
  );
};

export default HomePage;
